INSERT INTO `tb_article` VALUES (96, 1, 3, 'http://api.btstu.cn/sjbz/?lx=m_meizi&time=1515', 'Centos Linuxç³»ç»Ÿä½¿ç”¨vsftpdæ­å»ºftpæœåŠ¡', '<p>ï»¿## Linuxï¼ŒCentosç³»ç»Ÿä½¿ç”¨vsftpdæ­å»ºftpæœåŠ¡</p>\n<h3 id=\"ä¸€å®‰è£…vsftpd\">ä¸€ã€å®‰è£…vsftpd</h3>\n<ol>\n<li>è¿è¡Œä»¥ä¸‹å‘½ä»¤å®‰è£…vsftpdã€‚</li>\n</ol>\n<pre><code class=\"language-powershell\">yum install -y vsftpd\n</code></pre>\n<p>è¿”å›å¦‚ä¸‹å›¾æ‰€ç¤ºç•Œé¢æ—¶ï¼Œè¡¨ç¤ºå®‰è£…æˆåŠŸã€‚<br />\n<img src=\"https://img-blog.csdnimg.cn/20201011163459305.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L01hY1d4,size_16,color_FFFFFF,t_70#pic_center\" alt=\"åœ¨è¿™é‡Œæ’å…¥å›¾ç‰‡æè¿°\" /></p>\n<ol start=\"2\">\n<li>è¿è¡Œä»¥ä¸‹å‘½ä»¤è®¾ç½®FTPæœåŠ¡å¼€æœºè‡ªå¯åŠ¨ã€‚</li>\n</ol>\n<pre><code class=\"language-powershell\">systemctl enable vsftpd.service\n</code></pre>\n<ol start=\"3\">\n<li>å¯åŠ¨FTPæœåŠ¡ã€‚</li>\n</ol>\n<pre><code class=\"language-powershell\">systemctl start vsftpd.service\n</code></pre>\n<ol start=\"4\">\n<li>è¿è¡Œä»¥ä¸‹å‘½ä»¤æŸ¥çœ‹FTPæœåŠ¡ç›‘å¬çš„ç«¯å£ã€‚</li>\n</ol>\n<pre><code class=\"language-powershell\">netstat -antup | grep ftp\n</code></pre>\n<p>å‡ºç°å¦‚ä¸‹å›¾æ‰€ç¤ºç•Œé¢ï¼Œè¡¨ç¤ºFTPæœåŠ¡å·²å¯åŠ¨ï¼Œç›‘å¬çš„ç«¯å£å·ä¸º 21ã€‚æ­¤æ—¶ï¼Œvsftpdé»˜è®¤å·²å¼€å¯åŒ¿åè®¿é—®åŠŸèƒ½ï¼Œæ‚¨æ— éœ€è¾“å…¥ç”¨æˆ·åå¯†ç å³å¯ç™»å½•FTPæœåŠ¡å™¨ï¼Œä½†æ²¡æœ‰ä¿®æ”¹æˆ–ä¸Šä¼ æ–‡ä»¶çš„æƒé™ã€‚<br />\n<img src=\"https://img-blog.csdnimg.cn/20201011163555146.png#pic_center\" alt=\"åœ¨è¿™é‡Œæ’å…¥å›¾ç‰‡æè¿°\" /></p>\n<h3 id=\"äºŒé…ç½®vsftpd\">äºŒã€é…ç½®vsftpd</h3>\n<blockquote>\n<p>vsftpdï¼ˆvery secure FTP<br />\ndaemonï¼‰æ˜¯ä¸€æ¬¾åœ¨Linuxå‘è¡Œç‰ˆä¸­æœ€å—æ¨å´‡çš„FTPæœåŠ¡å™¨ã€‚vsftpdæ”¯æŒåŒ¿åè®¿é—®å’Œæœ¬åœ°ç”¨æˆ·æ¨¡å¼ä¸¤ç§è®¿é—®æ–¹å¼ã€‚åŒ¿åè®¿é—®æ–¹å¼ä»»ä½•ç”¨æˆ·éƒ½å¯ä»¥è®¿é—®æ­å»ºçš„FTPæœåŠ¡ï¼›æœ¬åœ°ç”¨æˆ·æ¨¡å¼åªæ”¯æŒæ·»åŠ çš„æœ¬åœ°ç”¨æˆ·è®¿é—®æ­å»ºçš„FTPæœåŠ¡ã€‚</p>\n</blockquote>\n<ul>\n<li>è¯´æ˜: åŒ¿åç”¨æˆ·æ¨¡å¼å’Œæœ¬åœ°ç”¨æˆ·æ¨¡å¼åªå¯åŒæ—¶é…ç½®ä¸€ç§ã€‚</li>\n</ul>\n<h4 id=\"21-åŒ¿åç”¨æˆ