INSERT INTO `tb_article` VALUES (191, 1, 2, 'http://api.btstu.cn/sjbz/?lx=m_meizi&time=657', 'spring boot å®ç°æ–‡ä»¶çš„ä¸Šä¼ ä¸‹è½½', '<p>ï»¿## spring bootå®ç°æ–‡ä»¶çš„ä¸Šä¼ ï¼Œ</p>\n<p>æˆ‘æ˜¯ä½¿ç”¨mavenæ­å»ºçš„spring booté¡¹ç›®ï¼Œé¡µé¢é›†æˆçš„jspã€‚<br />\né¡µé¢ç»“æ„å¦‚ä¸‹ï¼š<br />\n<img src=\"https://img-blog.csdnimg.cn/20190811160646759.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L01hY1d4,size_16,color_FFFFFF,t_70\" alt=\"åœ¨è¿™é‡Œæ’å…¥å›¾ç‰‡æè¿°\" /><br />\nå‡†å¤‡å·¥ä½œï¼šåœ¨webappä¸‹æ–°å»ºä¸€ä¸ªç›®å½•å«upload</p>\n<p><strong>1ï¼Œé¡µé¢ï¼Œupload.jsp</strong></p>\n<pre><code>&lt;body&gt;\n&lt;form method=&quot;post&quot; action=&quot;${pageContext.request.contextPath}/file/upload&quot; enctype=&quot;multipart/form-data&quot;&gt;\n    &lt;input type=&quot;file&quot; name=&quot;files&quot;&gt;\n    &lt;input type=&quot;submit&quot; value=&quot;ä¸Šä¼ &quot;&gt;\n&lt;/form&gt;\n&lt;/body&gt;\n</code></pre>\n<p><strong>2) æ§åˆ¶å™¨ ,FileController.java</strong></p>\n<pre><code>@Controller\n@RequestMapping(&quot;/file&quot;)\npublic class FileController {\n\n    private Logger logger = LoggerFactory.getLogger(getClass());\n\n    @RequestMapping(&quot;/upload&quot;)\n    public String upLoad(MultipartFile files, HttpSession session) throws IOException {\n        logger.info(&quot;upload æ–¹æ³•æ‰§è¡Œäº†&quot;);\n        //è·å–ä¸Šä¼ æ–‡ä»¶çš„è·¯å¾„\n        String realPath = session.getServletContext().getRealPath(&quot;upload&quot;);\n        System.out.println(&quot;--------&quot;+realPath);\n        //è·å–ä¸Šä¼ çš„æ–‡ä»¶åï¼Œ\n        File file1 = new File(realPath, files.getOriginalFilename());\n        //æ‰§è¡Œæ–‡ä»¶ä¸Šä¼ å¤åˆ¶\n        files.transferTo(file1);\n        //è·³åˆ°ä¸‹è½½é¡µé¢\n        return &quot;/download.jsp&quot;;\n\n    }\n}\n</code></pre>\n<p><strong>3) ä¿®æ”¹ä¸Šä¼ æ–‡ä»¶å¤§å°çš„é™åˆ¶ï¼Œapplication.properties</strong></p>\n<pre><code>//é…ç½®tomcatç«¯å£å·\nserver.port=8080\n//é¡¹ç›®è®¿é—®è·¯å¾„