INSERT INTO `tb_article` VALUES (214, 1, 2, 'http://api.btstu.cn/sjbz/?lx=m_meizi&time=6720', 'Spring Boot å°è£…æ–‡ä»¶ä¸Šä¼ å·¥å…·ç±»', '<p>ï»¿springboot å®ç°æ–‡ä»¶çš„ä¸Šä¼ è¿™é‡Œæˆ‘ç”¨çš„æ˜¯commins-ioä¾èµ–ï¼š<br />\nåæ ‡å¦‚ä¸‹ï¼š</p>\n<pre><code class=\"language-java\"> &lt;dependency&gt;\n      &lt;groupId&gt;commons-io&lt;/groupId&gt;\n      &lt;artifactId&gt;commons-io&lt;/artifactId&gt;\n      &lt;version&gt;2.4&lt;/version&gt;\n    &lt;/dependency&gt;\n</code></pre>\n<p>å¼•å…¥è¿™ä¸ªä¾èµ–ä¹‹åå°±å¯ä»¥ä½¿ç”¨ä¸‹é¢è¿™ä¸ªå·¥å…·ç±»è¿›è¡Œæ–‡ä»¶ä¸Šä¼ çš„æ“ä½œäº†ï¼š<br />\nå¦‚ä¸‹ï¼š</p>\n<pre><code class=\"language-java\">package com.tourism.fzll.util;\n\nimport java.io.File;\nimport java.util.UUID;\n\nimport javax.servlet.http.HttpServletRequest;\n\nimport org.springframework.web.context.request.RequestContextHolder;\nimport org.springframework.web.context.request.ServletRequestAttributes;\nimport org.springframework.web.multipart.MultipartFile;\n\npublic class FileUploadUtil {\n	\n	public static String upload(MultipartFile file) {\n		try {\n			String extName = file.getOriginalFilename();\n			// è·å–æ–‡ä»¶åç¼€\n			if (extName.lastIndexOf(&quot;.&quot;) &lt;= 0) {\n				throw new RuntimeException(&quot;ä¸æ”¯æŒè¯¥æ–‡ä»¶ç±»å‹&quot;);\n			}\n			extName = extName.substring(extName.lastIndexOf(&quot;.&quot;));\n			String webUrl = getWebUrl();\n			String fileName = getFileName();\n			String sysPath = System.getProperty(&quot;catalina.home&quot;) + &quot;/webapps&quot;;\n			// è·å–æ–‡ä»¶åå­—\n			fileName = getFileName() + extName;\n			// è·å–æ–‡ä»¶åœ°å€\n			String filePath = &quot;/content/&quot; + fileName;\n			String Url = sysPath +&quot;/content/&quot;;\n			File file2 = new File(Url);\n			if (!file2.exists()) {\n				file2.mkdirs();\n			}\n			file.transferTo(new File(sysPath + filePath));\n			return webUrl + filePath;\n		} catch (Exception e) {\n			throw new RuntimeException(e);\n		}\n	}\n	/**\n	 * \n	 * @Title: upload \n	 * @Description: (å°†æ–‡ä»¶ä¿å­˜åˆ°æŒ‡å®šçš„è·¯å¾„ä¸‹) \n	 * @param file\n\n	 * @return  \n	 * @date 2019å¹´9æœˆ30æ—¥ ä¸Šåˆ10:22:31\n	 * @author é©¬è¶…ä¼Ÿ\n	 */\n	public static String upload(MultipartFile file,String specifiedPath) {\n		try {\n			String extName = file.getOriginalFilename();\n			// è·å–æ–‡ä»¶åç¼€\n			if (extName.lastIndexOf(&quot;.&quot;) &lt;= 0) {\n				throw new RuntimeException(&quot;ä¸æ”¯æŒè¯¥æ–‡ä»¶ç±»å‹&quot;);\n			}\n			extName = extName.substring(extName.lastIndexOf(&quot;.&quot;));\n			String fileName = getFileName();\n			// è·å–æ–‡ä»¶åå­—\n			fileName = getFileName() + extName;\n			File file2 = new File(specifiedPath);\n			if (!file2.exists()) {\n				file2.mkdirs();\n			}\n			file.transferTo(new File(specifiedPath + File.separator+ fileName));\n			return fileName;\n		} catch (Exception e) {\n			throw new RuntimeException(e);\n		}\n	}\n\n	/**\n	 * è·å–æ–‡ä»¶å\n	 * @return\n	 */\n	public static String getFileName() {\n		String uuid = UUID.randomUUID().toString();\n		uuid = uuid.replace(&quot;-&quot;, &quot;&quot;);\n		return uuid.toLowerCase();\n	}\n\n	public static String getWebUrl() {\n		HttpServletRequest request = ((ServletRequestAttributes) RequestContextHolder.getRequestAttributes()).getRequest();\n		return  request.getServletContext().getRealPath(&quot;/img&quot;);\n	}\n	\n	public static String getWebProUrl() {\n		HttpServletRequest request = ((ServletRequestAttributes) RequestContextHolder.getRequestAttributes()).getRequest();\n		return request.getScheme() + &quot;://&quot; + request.getServerName() + &quot;:&quot; + request.getServerPort() +request.getContextPath();\n	}\n	\n}\n\n</code></pre>\n', 1, '', 0, 0, 1, '2020-04-25 15:37:53', '2021-08-30 10:00:30');
