INSERT INTO `tb_article` VALUES (259, 1, 1, 'http://api.btstu.cn/sjbz/?lx=m_meizi&time=4516', 'mysqlæ ¹æ®çˆ¶èŠ‚ç‚¹é€’å½’æŸ¥è¯¢æ‰€æœ‰å­èŠ‚ç‚¹ï¼ŒListè½¬æ ‘å½¢ç»“æ„å·¥å…·ç±»', '<p>ï»¿&gt; ç»å¸¸æœ‰ä¸šåŠ¡éœ€è¦é€’å½’æŸ¥è¯¢MySQLæ ‘å½¢ç»“æ„æŸä¸€èŠ‚ç‚¹çš„æ‰€æœ‰å­èŠ‚ç‚¹ï¼Œæ¯æ¬¡ä»ç½‘ä¸Šæ‰’æ‹‰æ‰¾éƒ½å¾—æ‰¾åŠå¤©ï¼Œç´¢æ€§å°±è‡ªå·±æ€»ç»“ä¸€äº›è‡ªå·±é‡åˆ°çš„æ¯”è¾ƒå¥½ç”¨çš„ä¸€äº›æ–¹æ³•ã€‚</p>\n<h1 id=\"sqlæ–¹æ³•ä¸€\">SQLæ–¹æ³•ä¸€ï¼š</h1>\n<pre><code class=\"language-sql\">SELECT\n	* \nFROM\n	(\n	SELECT\n		t1.*,\n	IF\n		( FIND_IN_SET( parent_id, @parent_ids ) &gt; 0, @parent_ids := CONCAT( @parent_ids, \',\', id ), \'0\' ) AS ischild \n	FROM\n		( SELECT * FROM blade_menu AS t WHERE t.is_deleted = 0 AND client_type = \'dianduyun_app\' ORDER BY t.id ASC ) t1,\n		( SELECT @parent_ids := \'1508255373140103170\' ) t2 \n	) t3 \nWHERE\n	ischild != \'0\'\n</code></pre>\n<h2 id=\"éœ€è¦ä¿®æ”¹çš„åœ°æ–¹\">éœ€è¦ä¿®æ”¹çš„åœ°æ–¹</h2>\n<ul>\n<li><em><strong>parent_id</strong></em>ï¼Œæ”¹ä¸ºä½ çš„çˆ¶çº§idå­—æ®µ</li>\n<li><em><strong>blade_menu</strong></em> ï¼Œæ”¹ä¸ºä½ çš„è¡¨åç§°</li>\n<li>WHERE <em><strong>t.is_deleted = 0</strong></em> ï¼Œæ”¹ä¸ºä½ çš„æŸ¥è¯¢æ¡ä»¶</li>\n<li>@parent_ids := \'<em><strong>1508255373140103170</strong></em>\'ï¼Œå€¼æ”¹ä¸ºä½ è¦ä¼ å…¥çš„çˆ¶çº§id</li>\n</ul>\n<h2 id=\"æ‰§è¡Œç»“æœ\">æ‰§è¡Œç»“æœ</h2>\n<p><img src=\"https://img-blog.csdnimg.cn/87dc6bdc02a44b61b9396b95661a6443.png\" alt=\"åœ¨è¿™é‡Œæ’å…¥å›¾ç‰‡æè¿°\" /></p>\n<h1 id=\"sqlæ–¹æ³•äºŒ\">SQLæ–¹æ³•äºŒï¼š</h1>\n<pre><code class=\"language-sql\">SELECT\n	rd.* \nFROM\n	( SELECT * FROM fine_authority_object WHERE parentId IS NOT NULL AND is_deleted = 0 ) rd,\n	( SELECT @pid := \'2342465874553522423\' ) pd \nWHERE\n	FIND_IN_SET( parentId, @pid ) &gt; 0 \n	AND @pid := concat( @pid, \',\', id ) \n\nUNION\nSELECT\n	* \nFROM\n	fine_authority_object \nWHERE\n	FIND_IN_SET( id, @pid ) &gt; 0;\n</code></pre>\n<h2 id=\"éœ€è¦ä¿®æ”¹çš„åœ°æ–¹-1\">éœ€è¦ä¿®æ”¹çš„åœ°æ–¹</h2>\n<ul>\n<li><em><strong>fine_authority_object</strong></em> ï¼Œæ”¹ä¸ºä